node {

    CStringView _wait = "‚åõ";
    CStringView _device = "üìü";
    CStringView _ok = "‚úîÔ∏è";
    CStringView _error = "‚ùå";
    char str[16];
    CStringView view = CStringView(str);

    uint8_t addr = 0; //Address start
    uint8_t dev = 0;
    uint8_t _res = -1; //flag device found
    int8_t count = 0;

    void evaluate(Context ctx) {

        if(isInputDirty<input_RST>(ctx)) {
            addr = 0;
            emitValue<output_DEV1>(ctx, 0);
            emitValue<output_DEV2>(ctx, 0);
            emitValue<output_DEV3>(ctx, 0);
            emitValue<output_DEV4>(ctx, 0);
            emitValue<output_DEV5>(ctx, 0);
        }

        if (!isInputDirty<input_SCAN>(ctx)){return;}

        auto dev1 = getValue<output_DEV1>(ctx);
        auto dev2 = getValue<output_DEV2>(ctx);
        auto dev3 = getValue<output_DEV3>(ctx);
        auto dev4 = getValue<output_DEV4>(ctx);

        addr++;
        if(_res == 0)
            delay(1500);

        if (addr > 127) {
            if(dev > 0){
                emitValue<output_OUT1>(ctx, XString(&_ok));
                addr = 127;
            }else{
                emitValue<output_OUT1>(ctx, XString(&_error));
            }return;}

            formatNumber(addr, 0, str);
            emitValue<output_Scanning>(ctx, XString(&view)); // 0 to 127
            auto wire = getValue<input_I2C>(ctx);
            wire->beginTransmission(addr);
            auto res = wire->endTransmission();
            if (res == 0) {
                _res = res;
                emitValue<output_OUT1>(ctx, XString(&_device)); //I2C found
                emitValue<output_DEV5>(ctx, dev4);
                emitValue<output_DEV4>(ctx, dev3);
                emitValue<output_DEV3>(ctx, dev2);
                emitValue<output_DEV2>(ctx, dev1);
                emitValue<output_DEV1>(ctx, addr);
                dev++;
            }else{
                _res = res;
                emitValue<output_OUT1>(ctx, XString(&_wait)); //Wait
            }
    }
}
