void evaluate(Context ctx) {
    auto state = getState(ctx);
    auto blynk = getValue<input_BLNK>(ctx);
    uint8_t vpin = getValue<input_VPIN>(ctx);

    if (isInputDirty<input_INIT>(ctx)){
        uint8_t vpin = getValue<input_VPIN>(ctx);
        blynk.xodBlynk->virtualWrite(vpin, 255);
