#pragma XOD require "https://github.com/adafruit/Adafruit_TCS34725"

// clang-format off
{{#global}}
#include <Adafruit_TCS34725.h>
{{/global}}
// clang-format on

struct State {
    Adafruit_TCS34725* tcs;
    uint8_t mem[sizeof(Adafruit_TCS34725)];
};

// clang-format off
{{ GENERATED_CODE }}
// clang-format on

void evaluate(Context ctx) {
    auto state = getState(ctx);
    if (isInputDirty<input_INIT>(ctx)){
        uint8_t iteration = getValue<input_IT>(ctx);
        uint8_t gain = getValue<input_GAIN>(ctx);
        tcs34725Gain_t gainVal;
        tcs34725IntegrationTime_t itVal;
        switch (iteration){
            case 1: itVal = TCS34725_INTEGRATIONTIME_2_4MS; break;      /**<  2.4ms - 1 cycle    - Max Count: 1024  */
            case 10: itVal = TCS34725_INTEGRATIONTIME_24MS; break;      /**<  24ms  - 10 cycles  - Max Count: 10240 */
            case 20: itVal = TCS34725_INTEGRATIONTIME_50MS; break;      /**<  50ms  - 20 cycles  - Max Count: 20480 */
            case 42: itVal = TCS34725_INTEGRATIONTIME_101MS; break;     /**<  101ms - 42 cycles  - Max Count: 43008 */
            case 64: itVal = TCS34725_INTEGRATIONTIME_154MS; break;     /**<  154ms - 64 cycles  - Max Count: 65535 */
            case 256: itVal = TCS34725_INTEGRATIONTIME_700MS; break;    /**<  700ms - 256 cycles - Max Count: 65535 */
            default: itVal = TCS34725_INTEGRATIONTIME_2_4MS;
        }
        switch (gain){
            case 1: gainVal = TCS34725_GAIN_1X; break; /**<  No gain  */
            case 4: gainVal = TCS34725_GAIN_4X; break; /**<  2x gain  */
            case 16: gainVal = TCS34725_GAIN_16X; break; /**<  16x gain  */
            case 60: gainVal = TCS34725_GAIN_60X; break; /**<  60x gain  */
            default: gainVal = TCS34725_GAIN_1X;
        }

        state->tcs =  new Adafruit_TCS34725();
        state->tcs->setIntegrationTime(itVal);
        state->tcs->setGain(gainVal);
        if (!state->tcs->begin()) {
             emitValue<output_ERR>(ctx, 1);
        }
    }
    if (isInputDirty<input_UPD>(ctx)){
        uint16_t red, green, blue, clear;
        state->tcs->getRawData(&red, &green, &blue, &clear);
        uint32_t sum = clear;
        float r, g, b;
        r = red; r /= sum;
        g = green; g /= sum;
        b = blue; b /= sum;
        r *= 255; g *= 255; b *= 255;
        emitValue<output_R>(ctx, (uint8_t)r);
        emitValue<output_G>(ctx, (uint8_t)g);
        emitValue<output_B>(ctx, (uint8_t)b);
        emitValue<output_DONE>(ctx, 1);
    }
}

