    auto obj = getValue<input_IN>(ctx);
    uint8_t size = obj.size;
    for (uint8_t i = 0; i <= size; i++){
        obj.ws2811->setPixelColor(i, obj.ws2811->Color(getValue<input_R>(ctx),getValue<input_G>(ctx),getValue<input_B>(ctx)));
    }
    emitValue<output_OUT>(ctx, obj);
    emitValue<output_DONE>(ctx, 1);
}

