// Tell XOD where it could download the library:
#pragma XOD require "https://github.com/Makeblock-official/Makeblock-Libraries"

//Include C++ libraries
#include <MeMCore.h>
#include <MePort.h>
#include <MeDCMotor.h>

node {
    meta {
        // Define our custom type as a pointer on the class instance.
        using Type = MeDCMotor*;
    }

    uint8_t mem[sizeof(MeDCMotor)];

    void evaluate(Context ctx) {
        // It should be evaluated only once on the first (setup) transaction
        if (!isSettingUp())
            return;

        Type motor1 = new (mem) MeDCMotor(M1);

        emitValue<output_DEV>(ctx, motor1);
    }
}

