// Tell XOD where it could download the library:
#pragma XOD require "https://github.com/Makeblock-official/Makeblock-Libraries"

//Include C++ libraries
#include <MeMCore.h>
#include <MePort.h>
#include <MeUltrasonicSensor.h>

node {
    meta {
        // Define our custom type as a pointer on the class instance.
        using Type = MeUltrasonicSensor*;
    }

    uint8_t mem[sizeof(MeUltrasonicSensor)];

    void evaluate(Context ctx) {
        // It should be evaluated only once on the first (setup) transaction
        if (!isSettingUp())
            return;

        auto port = getValue<input_Port>(ctx);

         if (port == 1)
             port = PORT_1;
         if (port == 2)
             port = PORT_2;
         if (port == 3)
             port = PORT_3;
         if (port == 4)
             port = PORT_4;


        Type sensor = new (mem) MeUltrasonicSensor(port);

        emitValue<output_DEV>(ctx, sensor);
    }
}

