           return;


        auto display = getValue<input_DEV>(ctx);
        auto bright = getValue<input_bright>(ctx);

        if (bright == 0)
            bright = Brightness_0;
        if (bright == 1)
            bright = Brightness_1;
        if (bright == 2)
            bright = Brightness_2;
        if (bright == 3)
            bright = Brightness_3;
        if (bright == 4)
            bright = Brightness_4;
        if (bright == 5)
            bright = Brightness_5;
        if (bright == 6)
            bright = Brightness_6;
        if (bright == 7)
            bright = Brightness_7;
        if (bright == 8)
            bright = Brightness_8;

        if (!isInputDirty<input_Clear>(ctx))
        display-> clearScreen();

        display-> setBrightness(bright);

        emitValue<output_Done>(ctx, 1);
   }
}

