#pragma XOD require "https://github.com/me-no-dev/ESPAsyncWebServer"
#pragma XOD require "https://github.com/me-no-dev/AsyncTCP"

#pragma XOD error_raise enable

#include <WiFi.h>
#include <AsyncTCP.h>
#include <ESPAsyncWebServer.h>

AsyncWebServer server(80);

const char index_html[] PROGMEM = R"rawliteral(
    <!DOCTYPE HTML><html>
    <head>
      <meta name="viewport" content="width=device-width, initial-scale=1">
      <link rel="icon" href="data:,">
       %TITLE%
       %STYLE%
    </head>
    <body>
        <div id="operation">%OPERATION%</div>
    </body>
    <script>
    setInterval(function ( ) {
      var xhttp = new XMLHttpRequest();
      xhttp.onreadystatechange = function() {
        if (this.readyState == 4 && this.status == 200) {
          document.getElementById("operation").innerHTML = this.responseText;
        }
      };
      xhttp.open("GET", "/operation", true);
      xhttp.send();
    }, 2000 ) ;
    </script>
    </html>
    )rawliteral";

namespace input {
    String operation = "";
    String style = "";
    String title = "";
}

String processor(const String& var){
      if(var == "TITLE"){
        return String(input::title);
      } else if(var == "STYLE"){
        return String(input::style);
      } else if(var == "OPERATION"){
        return String(input::operation);
      }
      return String();
}

node {
    #define WIFI_TIMEOUT_MS 20000

    void evaluate(Context ctx) {
        if (isInputDirty<input_CONN>(ctx)){

            auto tt = getValue<input_TITLE>(ctx);
            char _tt[length(tt) + 1] = { 0 };
            dump(tt, _tt);
            int lengthtt = length(tt) + 1;

            String ttl = "";
            for (int i = 0; i < lengthtt; i++) {
                ttl += _tt[i];
            }
            input::title = "<title>" + ttl + "</title>";

            server.on("/", HTTP_GET, [](AsyncWebServerRequest *request){
              request->send_P(200, "text/html", index_html, processor);
            });

            server.on("/operation", HTTP_GET, [](AsyncWebServerRequest *request){
                request->send_P(200, "text/plain", String(input::operation).c_str());
            });

            server.begin();

            auto mode = WiFi.getMode();
            if(WiFi.getMode() == 1) {
                auto ip = WiFi.localIP();
                emitValue<output_IP>(ctx, ip);
            }
            emitValue<output_DONE>(ctx, 1);

        }

        if (isInputDirty<input_RUN>(ctx)){
            auto op = getValue<input_OP>(ctx);
            char _op[length(op) + 1] = { 0 };
            dump(op, _op);
            int lengthchar = length(op) + 1;

            String opp = "";
            for (int i = 0; i < lengthchar; i++) {
                opp += _op[i];
            }
            input::operation = opp;

            auto st = getValue<input_STYLE>(ctx);
            char _st[length(st) + 1] = { 0 };
            dump(st, _st);
            int lengthst = length(st) + 1;

            String sty = "";
            for (int i = 0; i < lengthst; i++) {
                sty += _st[i];
            }
            input::style = sty;
        }


    }
}

#pragma XOD require "https://github.com/espressif/arduino-esp32"

#pragma XOD error_raise enable

#include <WiFi.h>
#include <WiFiServer.h>

WiFiServer wifiServer(80);

node {
    String request = "";
    void evaluate(Context ctx) {
        if (isInputDirty<input_CONN>(ctx)) {
            wifiServer.begin();

            auto ip = WiFi.localIP();
            emitValue<output_IP>(ctx, ip);
        }

        if (isInputDirty<input_RUN>(ctx)) {
            WiFiClient client = wifiServer.available();
            if(!client)
                return;

            boolean currentLineIsBlank = true;
            while (client.connected())
            {
                if(client.available())
                {
                  char c = client.read();
                  request += c;
                  Serial.write(c);
                  if (c == '\n' && currentLineIsBlank)
                  {
                    client.println("HTTP/1.1 200 OK");
                    client.println("Content-Type: text/html");
                    client.println("Connection: close");
                    client.println();

                    client.println("<!DOCTYPE HTML>");
                    client.println("<html>");

                    client.println("<head>");
                    client.println("<title>");

                    auto title = getValue<input_TITLE>(ctx);
                    char _title[length(title) + 1] = { 0 };
                    dump(title, _title);
                    client.println(_title);

                    delay(2000);

                    client.println("</title>");
                    client.println("<meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">");
                    client.println("<link rel=\"icon\" href=\"data:,\">");

                    auto style = getValue<input_STYLE>(ctx);
                    char _style[length(style) + 1] = { 0 };
                    dump(style, _style);
                    client.println(_style);
                    delay(2000);

                    client.println("</head>");

                    client.println("<body>");
                    client.println("<div id=\"operation\">");

                    auto op = getValue<input_OP>(ctx);
                    char _op[length(op) + 1] = { 0 };
                    dump(op, _op);
                    client.println(_op);

                    client.println("</div>");

                    client.println("</body>");

                    client.println("</html>");

                    break;
                  }
                  if(c == '\n')
                  {
                    currentLineIsBlank = true;
                  }
                  else if(c != '\r')
                  {
                    currentLineIsBlank = false;
                  }
                }
              }
        }

        emitValue<output_DONE>(ctx, 1);

    }
}
