// Tell XOD where it can download the libraries:
#pragma XOD require "https://github.com/adafruit/Adafruit_ADS1X15"

//Include C++ libraries
#include <Adafruit_ADS1X15.h>
#include <Wire.h>

node {

    meta {
        // Define our custom type as a pointer on the class instance.
        using Type = Adafruit_ADS1115*;
    }

    // Create an object of class Adafruit_ADS1115
    Adafruit_ADS1115 sensor = Adafruit_ADS1115();

    void evaluate(Context ctx) {
        // It should be evaluated only once on the first (setup) transaction
        if (!isSettingUp())
            return;

        auto wire = getValue<input_I2C>(ctx);
        auto addr = getValue<input_ADDR>(ctx);

        // Try to initialize sensor
        if (!sensor.begin(addr, wire)) {
            raiseError(ctx);
            return;
        }
        emitValue<output_DEV>(ctx, &sensor);
    }
}

node {
    void evaluate(Context ctx) {
        // The node responds only if there is an input pulse
        if (!isInputDirty<input_UPD>(ctx))
            return;

        // Get a pointer to the `Adafruit_ADS1115` class instance
        auto sensor = getValue<input_DEV>(ctx);
        int16_t raw = getValue<input_ADC>(ctx);

        emitValue<output_Volts>(ctx, sensor->computeVolts(raw));
        emitValue<output_Done>(ctx, 1);

    }
}

node {
    void evaluate(Context ctx) {
        // The node responds only if there is an input pulse
        if (!isInputDirty<input_UPD>(ctx))
            return;

        // Get a pointer to the `Adafruit_ADS1115` class instance
        auto sensor = getValue<input_DEV>(ctx);
        adsGain_t gain = sensor->getGain();

        if (gain==GAIN_TWOTHIRDS) {
            emitValue<output_Gain>(ctx, 0.67);
        }
        else if (gain==GAIN_ONE) {
            emitValue<output_Gain>(ctx, 1);
        }
        else if (gain==GAIN_TWO) {
            emitValue<output_Gain>(ctx, 2);
        }
        else if (gain==GAIN_FOUR) {
            emitValue<output_Gain>(ctx, 4);
        }
        else if (gain==GAIN_EIGHT) {
            emitValue<output_Gain>(ctx, 8);
        }
        else if (gain==GAIN_SIXTEEN) {
            emitValue<output_Gain>(ctx, 16);
        }
        emitValue<output_Done>(ctx, 1);
    }
}

