            return;

        // Get a pointer to the `EasyNex` class instance
        auto eznex = getValue<input_DEV>(ctx);

        uint8_t triggerID = eznex -> NextionListen();

        if (triggerID < 255) {
            emitValue<output_TrigID>(ctx, triggerID);
