#pragma XOD evaluate_on_pin disable
#pragma XOD evaluate_on_pin enable input_UPD

node {

    void evaluate(Context ctx) {
        // The node responds only if there is an input pulse
        if (!isInputDirty<input_UPD>(ctx))
            return;

        auto sensor = getValue<input_DEV>(ctx);

        emitValue<output_Prec>(ctx, sensor->getPrecision());
        emitValue<output_Done>(ctx, 1);
    }
}

#pragma XOD evaluate_on_pin disable
#pragma XOD evaluate_on_pin enable input_UPD

node {

    void evaluate(Context ctx) {
        // The node responds only if there is an input pulse
        if (!isInputDirty<input_UPD>(ctx))
            return;

        auto sensor = getValue<input_DEV>(ctx);
        auto prec = getValue<input_Prec>(ctx);

        if (prec < 3) {
            sensor->setPrecision(prec);
            emitValue<output_Done>(ctx, 1);
        }

    }
}

// Tell XOD where it can download the libraries:
#pragma XOD require "https://github.com/adafruit/Adafruit_BusIO"
#pragma XOD require "https://github.com/adafruit/Adafruit_Sensor"
#pragma XOD require "https://github.com/adafruit/Adafruit_SHT4X"

//Include C++ libraries
#include <Adafruit_SHT4x.h>

node {
    meta {
        // Define our custom type as a pointer on the class instance.
        using Type = Adafruit_SHT4x*;
    }
    // Keep Adafruit_SHT4x object in state
    Adafruit_SHT4x sensor = Adafruit_SHT4x();

    void evaluate(Context ctx) {
        // It should be evaluated only once on the first (setup) transaction
        if (!isSettingUp())
            return;
        emitValue<output_DEV>(ctx, &sensor);
    }
}

#pragma XOD evaluate_on_pin disable
#pragma XOD evaluate_on_pin enable input_INIT

node {

    void evaluate(Context ctx) {

        // The node responds only if there is an input pulse
        if (!isInputDirty<input_INIT>(ctx))
            return;

        auto sensor = getValue<input_DEV>(ctx);
        auto wire = getValue<input_I2C>(ctx);

        // Attempt to initialize sht4x module; if attempt fails emit error
        if (!sensor->begin(wire)) {
            raiseError(ctx);
            return;
        }

        emitValue<output_OK>(ctx, 1);
    }
}

#pragma XOD evaluate_on_pin disable
#pragma XOD evaluate_on_pin enable input_UPD

node {

    void evaluate(Context ctx) {
        // The node responds only if there is an input pulse
        if (!isInputDirty<input_UPD>(ctx))
            return;

        sensors_event_t humidity, temp;

        auto sensor = getValue<input_DEV>(ctx);
        sensor->getEvent(&humidity, &temp);

        emitValue<output_RH>(ctx, humidity.relative_humidity);
        emitValue<output_Temp>(ctx, temp.temperature);
        emitValue<output_Done>(ctx, 1);
    }
}

#pragma XOD evaluate_on_pin disable
#pragma XOD evaluate_on_pin enable input_UPD

node {

    void evaluate(Context ctx) {
        // The node responds only if there is an input pulse
        if (!isInputDirty<input_UPD>(ctx))
            return;

        auto sensor = getValue<input_DEV>(ctx);
        auto heater = getValue<input_Heat>(ctx);

        if (heater < 7) {
            sensor->setHeater(heater);
            emitValue<output_Done>(ctx, 1);
        }

        emitValue<output_Done>(ctx, 1);
    }
}

