#pragma XOD require "https://github.com/Seeed-Studio/Seeed_Arduino_Histogram"

//#include <SPI.h>
#include"Histogram.h"

node {
    meta {
        using Type = TFT_Histogram*;
    }

    TFT_Histogram barchart = TFT_Histogram();

    void evaluate(Context ctx) {
        if (!isSettingUp())
            return;

        auto tft = getValue<input_DEV>(ctx);
        barchart.initHistogram(tft);

        emitValue<output_BChart>(ctx, &barchart);
    }
}

#pragma XOD evaluate_on_pin disable
#pragma XOD evaluate_on_pin enable input_UPD

node {
    void evaluate(Context ctx) {
        // The node responds only if there is an input pulse
        if (!isInputDirty<input_UPD>(ctx))
            return;

        // Get a pointer to the `TFT_eSPI tft` class instance
        auto barchart = getValue<input_BChart>(ctx);

        uint8_t idx = getValue<input_Index>(ctx);
        uint32_t width = getValue<input_Width>(ctx);

        barchart -> changeWidth(idx, width);

        emitValue<output_Done>(ctx, 1);
    }
}

