node {
    void evaluate(Context ctx) {
        auto dev = getValue<input_DEV>(ctx);
        if (isInputDirty<input_DO>(ctx)) {
            dev->fill(0, 0, 0);
            emitValue<output_DONE>(ctx, 1);
        }
        emitValue<output_DEVU0027>(ctx, dev);
    }
}

#include <XColorPattern.h>

node {
    meta {
        using Type = Pattern*;
    }

    Pattern pattern;

    void evaluate(Context ctx) {
        emitValue<output_PAT>(ctx, &pattern);
    }
}

node {
    void evaluate(Context ctx) {
        auto dev = getValue<input_DEV>(ctx);

        if (isInputDirty<input_DO>(ctx)) {
            auto pat = getValue<input_PAT>(ctx);
            uint32_t shift = (uint32_t)getValue<input_SHFT>(ctx);
            dev->fillPattern(pat, shift);
        }

        emitValue<output_DEVU0027>(ctx, dev);
    }
}

node {
    void evaluate(Context ctx) {
        auto dev = getValue<input_DEV>(ctx);
        if (isInputDirty<input_DO>(ctx)) {
            auto color = getValue<input_C>(ctx);
            auto num = getValue<input_NUM>(ctx);
            if (isinf(num)) {
                // Fill all the LEDs
                dev->fill(color);
            } else {
                // Fill only specified number of LEDs
                bool fromTail = num < 0;
                uint32_t pixelsCount = (uint32_t) abs(num);
                dev->fill(color, pixelsCount, fromTail);
            }

            emitValue<output_DONE>(ctx, 1);
        }
        emitValue<output_DEVU0027>(ctx, dev);
    }
}

#include <XColorPattern.h>

node {
    PatternNode node;

    void evaluate(Context ctx) {
        auto pattern = getValue<input_PAT>(ctx);
        auto color = getValue<input_C>(ctx);

        if (isInputDirty<input_C>(ctx)) {
            node.setColor(color);
        }
        if (isSettingUp()) {
            pattern->add(&node);
        }

        emitValue<output_PATU0027>(ctx, pattern);
    }
}

node {
    void evaluate(Context ctx) {
        // Storing all colors of "previous" pattern
        // to compare with the "next" one is uneffective
        // by the RAM. So it expects that the same pattern
        // will not be sent at all.

        if (isInputDirty<input_IN>(ctx))
            emitValue<output_OUT>(ctx, 1);
    }
}

#include <WS2812.h>

node {
    static_assert(isValidDigitalPort(constant_input_PORT), "must be a valid digital port");

    meta {
        using Type = WS2812*;
    }

    uint8_t mem[sizeof(WS2812)];

    void evaluate(Context ctx) {
        if (!isSettingUp()) return;
        uint32_t w = (uint32_t)getValue<input_W>(ctx);
        Type t = new (mem) WS2812(constant_input_PORT, w);
        emitValue<output_DEV>(ctx, t);
    }
}
