node {
    void evaluate(Context ctx) {
        auto ip = getValue<input_IN>(ctx);

        auto octets = reinterpret_cast<uint8_t*>(&ip);

        emitValue<output_OUT1>(ctx, octets[0]);
        emitValue<output_OUT2>(ctx, octets[1]);
        emitValue<output_OUT3>(ctx, octets[2]);
        emitValue<output_OUT4>(ctx, octets[3]);
    }
}

node {
    meta {
        using Type = uint8_t*;
    }

    uint8_t mac[6];

    void evaluate(Context ctx) {
        mac[0] = getValue<input_IN1>(ctx);
        mac[1] = getValue<input_IN2>(ctx);
        mac[2] = getValue<input_IN3>(ctx);
        mac[3] = getValue<input_IN4>(ctx);
        mac[4] = getValue<input_IN5>(ctx);
        mac[5] = getValue<input_IN6>(ctx);

        emitValue<output_OUT>(ctx, mac);
    }
}

node {
    meta {
      // because most other IPAdress classes can cast from/to uint32_t
      using Type = uint32_t;
    }

    void evaluate(Context ctx) {
        uint32_t octet1 = getValue<input_IN1>(ctx);
        uint32_t octet2 = getValue<input_IN2>(ctx);
        uint32_t octet3 = getValue<input_IN3>(ctx);
        uint32_t octet4 = getValue<input_IN4>(ctx);

        Type ip = octet4 << 24 | octet3 << 16 | octet2 << 8 | octet1;
        emitValue<output_OUT>(ctx, ip);
    }
}

