            return;
        }

        uint8_t byte = 0x00;
        if (uart->readByte(&byte)) {
            emitValue<output_DONE>(ctx, 1);
            emitValue<output_BYTE>(ctx, byte);
        } else {
            raiseError(ctx); // Can't read byte
        }
    }
}

